// 项目操作按钮
.project-actions {
  display: flex;
  gap: 16rpx;

  .action-btn {
    width: 64rpx;
    height: 64rpx;
    border-radius: 16rpx;
    display: flex;
    align-items: center;
    justify-content: center;

